import { makeStyles } from '@material-ui/core/styles'

const styles = makeStyles((theme) => ({
	container: {
		width: '100%',
		minHeight: '100%',
		[theme.breakpoints.up('lg')]: {
			padding: theme.spacing(2, 0),
		},
    },
    backgroundImg:{
        flex : 1,
        width: '100%',
        height: '100%'
    }
}))
export default styles
