import React from 'react';
import backgroundImg from '../../assets/img/bg-login.jpg'
import { Paper, Card , Typography, TextField, FormControl, } from '@material-ui/core'
import Styles from './styles'
import { Formik, Form, Field } from 'formik'

const login = () => {
  const classes = Styles()
  const stylesPaper = {
        paperContainer: {
            backgroundImage: `url(${backgroundImg})`,
            backgroundPosition: 'center',
            backgroundSize: 'cover',
            backgroundRepeat: 'no-repeat',
            width: '100vw',
            height: '100vh',
        }
    };
  return (
    <Paper style={stylesPaper.paperContainer} className={classes.backgroundImg}>
        <Card>
        <Formik
			enableReinitialize
			initialValues={{ username: '', password: '' }}
			// validationSchema={schema}
			// onSubmit={(values) => handleSubmitLogin(values)}
		>
			{({ errors, handleSubmit, touched }) => {
				return (
					<Form
						onSubmit={handleSubmit}
						method='post'
						className={[classes.form, classes.p_4]}
					>
						<Typography
							className={[classes.title, classes.mb_3].join(' ')}
							text-align='center'
							variant='h3'
						>
							Masuk ke akun Anda
						</Typography>
						<Typography className={[classes.subtitle, classes.textLeft].join(' ')}>
							Selamat datang di{' '}
							{/* <Link className={classes.uri} onClick={() => handleNavigate('/')}>
								Mbiz.co.id
							</Link> */}
							<a className={classes.uri} href='/'>
								Mbiz.co.id
							</a>
							<br />
							Silakan tulis username dan kata sandi Anda dibawah ini untuk masuk.
						</Typography>
						<Field name='username'>
							{({
								field, // { name, value, onChange, onBlur }
								// form: {touched, errors}, // also values, setXXXX, handleXXXX, dirty, isValid, status, etc.
							}) => (
								<TextField
									variant='outlined'
									fullWidth
									size='small'
									margin='dense'
									placeholder='Username'
									type='input'
									{...field}
								/>
							)}
						</Field>

						<Field name='password'>
							{({ field }) => (
								<FormControl className={classes.password} variant='outlined'>
									<OutlinedInput
										id='outlined-adornment-password'
										type={showPassword ? 'text' : 'password'}
										placeholder='Kata Sandi'
										{...field}
										endAdornment={
											<InputAdornment position='end'>
												<IconButton
													aria-label='toggle password visibility'
													edge='end'
													onClick={() => callback()}
												>
													{showPassword ? <Visibility /> : <VisibilityOff />}
												</IconButton>
											</InputAdornment>
										}
									/>
								</FormControl>
							)}
						</Field>

						<Grid container>
							<Grid item xs={12} sm={12} className={classes.mb_3}>
								<Typography className={classes.forgotpass}>
									<Link
										className={classes.uri}
										onClick={() => handleNavigate('/forgot-password')}
									>
										Lupa Kata Sandi?
									</Link>
								</Typography>
							</Grid>
						</Grid>

						<Button
							fullWidth
							size='large'
							type='submit'
							variant='contained'
							color='secondary'
							className={classes.formButton}
							disabled={data.isLoading}
						>
							{data.isLoading ? <CircularProgress /> : 'Masuk'}
						</Button>

						<hr className={classes.hr_line}></hr>
						<Typography className={classes.textLeft}>
							<small>
								Belum mempunyai akun?
								<br />
								<Link
									className={classes.uri}
									onClick={() => handleNavigate('register')}
								>
									Klik disini
								</Link>{' '}
								untuk informasi pendaftaran
							</small>
						</Typography>
					</Form>
				)
			}}
		</Formik>
        </Card>
    </Paper>
  );
}

export default login;
